package JavaSessions;

public class LoopsAssignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		
		while(i>=0) {
			System.out.println(i);
			i--;
		}
		
		for (int o=1;o<=10;o++) {
			if (!(o%2==0)) {
				System.out.println(o);
			}
		}

	}

}
